import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserList.css'; // CSS dosyasını dahil ediyoruz.

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Kullanıcıları API'den çekiyoruz
  useEffect(() => {
    axios.get('https://localhost:7103/api/user')
      .then(response => {
        setUsers(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Kullanıcıları alırken hata oluştu');
        setLoading(false);
      });
  }, []);

  const handleDeleteUser = (id) => {
    axios.delete(`https://localhost:7103/api/user/${id}`)
      .then(response => {
        setUsers(users.filter(user => user.userId !== id)); // Silinen kullanıcıyı listeden çıkarıyoruz
      })
      .catch(err => {
        console.error('Kullanıcı silinirken hata:', err);
      });
  };

  if (loading) {
    return <div className="loading">Kullanıcılar yükleniyor...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="user-list-container">
      <h2 className="title">Kullanıcı Listesi</h2>
      <ul className="user-list">
        {users.map(user => (
          <li key={user.userId} className="user-item">
            <div className="user-info">
              <div className="info-item">
                <strong>Kullanıcı Adı:</strong>
                <span>{user.username}</span>
              </div>
              <div className="info-item">
                <strong>Email:</strong>
                <span>{user.email}</span>
              </div>
              <div className="info-item">
                <strong>Rol:</strong>
                <span>{user.role}</span>
              </div>
              <div className="info-item">
                <strong>Kullanıcı ID:</strong>
                <span>{user.userId}</span>
              </div>
              <div className="info-item">
                <strong>Satıcı ID:</strong>
                <span>{user.merchantId}</span>
              </div>
            </div>
            <button 
              className="delete-btn" 
              onClick={() => handleDeleteUser(user.userId)}
            >
              Sil
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserList;
