import React, { useState } from 'react';
import axios from 'axios';

const AddMerchant = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState(null);  // Error state ekleyelim
  const [loading, setLoading] = useState(false);  // Yükleniyor durumu ekleyelim

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const newMerchant = { name, email };
    setLoading(true);  // Yükleniyor durumunu aktif et

    try {
      const response = await axios.post('https://localhost:7103/api/merchant', newMerchant);
      console.log('Merchant added:', response.data);
      setName('');  // Formu sıfırlama
      setEmail(''); // Formu sıfırlama
      setError(null); // Hata mesajını sıfırlama
    } catch (err) {
      console.error('Error adding merchant:', err);
      setError('Merchant eklenirken bir hata oluştu!');  // Hata mesajını güncelleme
    } finally {
      setLoading(false);  // Yükleniyor durumunu bitir
    }
  };

  return (
    <div>
      <h2>Add Merchant</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input 
            type="text" 
            value={name} 
            onChange={(e) => setName(e.target.value)} 
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input 
            type="email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Adding...' : 'Add Merchant'}
        </button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}  {/* Hata mesajını göster */}
    </div>
  );
};

export default AddMerchant;
