import React, { useState } from 'react';
import axios from 'axios';
import './AddMerchant.css';  // CSS dosyasını import et

const AddMerchant = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [description, setDescription] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [isVerified, setIsVerified] = useState(false);
  const [profileImageUrl, setProfileImageUrl] = useState('');
  const [openingHours, setOpeningHours] = useState('');
  const [error, setError] = useState(null);  // Hata state'i
  const [loading, setLoading] = useState(false);  // Yükleniyor durumu

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const newMerchant = {
      name,
      description,
      email,
      phone,
      address,
      city,
      isVerified,
      profileImageUrl,
      openingHours
    };

    setLoading(true);

    try {
      const response = await axios.post('https://localhost:7103/api/merchant', newMerchant);
      console.log('Merchant added:', response.data);
      setName('');
      setEmail('');
      setDescription('');
      setPhone('');
      setAddress('');
      setCity('');
      setIsVerified(false);
      setProfileImageUrl('');
      setOpeningHours('');
      setError(null);  // Hata mesajını sıfırlama
    } catch (err) {
      console.error('Error adding merchant:', err);
      setError('Merchant eklenirken bir hata oluştu!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-merchant-container">
      <h2>Add Merchant</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input 
            type="text" 
            value={name} 
            onChange={(e) => setName(e.target.value)} 
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <textarea 
            value={description} 
            onChange={(e) => setDescription(e.target.value)} 
            maxLength="500"
          />
        </div>
        <div>
          <label>Email:</label>
          <input 
            type="email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required
          />
        </div>
        <div>
          <label>Phone:</label>
          <input 
            type="text" 
            value={phone} 
            onChange={(e) => setPhone(e.target.value)} 
          />
        </div>
        <div>
          <label>Address:</label>
          <input 
            type="text" 
            value={address} 
            onChange={(e) => setAddress(e.target.value)} 
          />
        </div>
        <div>
          <label>City:</label>
          <input 
            type="text" 
            value={city} 
            onChange={(e) => setCity(e.target.value)} 
          />
        </div>
        <div>
          <label>Is Verified:</label>
          <input 
            type="checkbox" 
            checked={isVerified} 
            onChange={(e) => setIsVerified(e.target.checked)} 
          />
        </div>
        <div>
          <label>Profile Image URL:</label>
          <input 
            type="text" 
            value={profileImageUrl} 
            onChange={(e) => setProfileImageUrl(e.target.value)} 
          />
        </div>
        <div>
          <label>Opening Hours:</label>
          <input 
            type="text" 
            value={openingHours} 
            onChange={(e) => setOpeningHours(e.target.value)} 
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Adding...' : 'Add Merchant'}
        </button>
      </form>
      {error && <p className="error-message">{error}</p>}
      {loading && <div className="loading-indicator">Adding Merchant</div>}
    </div>
  );
};

export default AddMerchant;
