import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Backend API URL'nizi güncelleyin
    axios.get('https://localhost:7103/api/product')
      .then(response => {
        setProducts(response.data);  // API'den gelen veriyi state'e kaydet
        setLoading(false);  // Yüklenme tamamlandı
      })
      .catch(err => {
        setError('Error fetching products');  // Hata mesajı
        setLoading(false);
        console.error('Error fetching products:', err);
      });
  }, []);

  if (loading) {
    return <p>Loading...</p>;  // Yükleniyor mesajı
  }

  if (error) {
    return <p>{error}</p>;  // Hata mesajı
  }

  return (
    <div>
      <h1>Products</h1>
      <div className="product-list">
        {products.map(product => (
          <div key={product.id} className="product-item">
            <img src={product.imageUrl} alt={product.name} />
            <h2>{product.name}</h2>
            <p>{product.price} USD</p>
            <button>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
