import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserList.css'; // CSS dosyasını dahil ediyoruz.

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Kullanıcıları API'den çekiyoruz
  useEffect(() => {
    axios.get('https://localhost:7103/api/user')
      .then(response => {
        setUsers(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Kullanıcıları alırken hata oluştu');
        setLoading(false);
      });
  }, []);

  const handleDeleteUser = (id) => {
    axios.delete(`https://localhost:7103/api/user/${id}`)
      .then(response => {
        setUsers(users.filter(user => user.userId !== id)); // Silinen kullanıcıyı listeden çıkarıyoruz
      })
      .catch(err => {
        console.error('Kullanıcı silinirken hata:', err);
      });
  };

  if (loading) {
    return <div className="loading">Kullanıcılar yükleniyor...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="user-list-container">
      <h2 className="title">Kullanıcı Listesi</h2>
      <ul className="user-list">
        {users.map(user => (
          <li key={user.userId} className="user-item">
            <div className="user-info">
              <p><strong>Kullanıcı Adı:</strong> {user.username}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>Rol:</strong> {user.role}</p>
              <p><strong>Kullanıcı ID:</strong> {user.userId}</p>
            </div>
            <button 
              className="delete-btn" 
              onClick={() => handleDeleteUser(user.userId)}
            >
              Sil
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserList;
