import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './MerchantList.css'; // CSS dosyasını dahil ediyoruz.

const MerchantList = () => {
  const [merchants, setMerchants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Satıcıları API'den çekiyoruz
  useEffect(() => {
    axios.get('https://localhost:7103/api/merchant')
      .then(response => {
        setMerchants(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Satıcıları alırken hata oluştu');
        setLoading(false);
      });
  }, []);

  const handleDeleteMerchant = (id) => {
    axios.delete(`https://localhost:7103/api/merchant/${id}`)
      .then(response => {
        // Silinen satıcıyı listeden çıkarıyoruz
        setMerchants(merchants.filter(merchant => merchant.merchantId !== id)); 
      })
      .catch(err => {
        console.error('Satıcı silinirken hata:', err);
      });
  };

  if (loading) {
    return <div className="loading">Satıcılar yükleniyor...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="merchant-list-container">
      <h2 className="title">Satıcı Listesi</h2>
      <ul className="merchant-list">
        {merchants.map(merchant => (
          <li key={merchant.merchantId} className="merchant-item">
            <span className="merchant-info">
              {merchant.name} - {merchant.email}
            </span>
            <button 
              className="delete-btn" 
              onClick={() => handleDeleteMerchant(merchant.merchantId)}
            >
              Sil
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MerchantList;
