// ProductList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductCard from './ProductCard';
import SearchAndFilter from './SearchAndFilter';
import './ProductList.css';

const ProductList = () => {
  const [products, setProducts] = useState([]); // Tüm ürünleri tutar
  const [filteredProducts, setFilteredProducts] = useState([]); // Filtrelenmiş ürünleri tutar
  const [loading, setLoading] = useState(true); // Yükleniyor durumu
  const [error, setError] = useState(null); // Hata mesajı

  // Veriyi backend'den alıyoruz
  useEffect(() => {
    axios.get('https://localhost:7103/api/product')
      .then(response => {
        console.log("Fetched products:", response.data); // Veriyi console'da kontrol et
        // setProducts(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Error fetching products');
        setLoading(false);
        console.error("Error fetching products:", err); // Hata mesajını kontrol et
      });
  }, []);
  

  // Arama fonksiyonu
  const handleSearch = (term) => {
    const filtered = products.filter(product =>
      product.name.toLowerCase().includes(term.toLowerCase()) ||
      (product.description && product.description.toLowerCase().includes(term.toLowerCase())) // Açıklamaya da arama ekleyelim
    );
    setFilteredProducts(filtered);
  };

  // Kategoriye göre filtreleme
  const handleFilter = (category) => {
    const filtered = category
      ? products.filter(product => product.category.toLowerCase() === category.toLowerCase())
      : products; // Eğer kategori boşsa, tüm ürünleri göster
    setFilteredProducts(filtered);
  };

  // Sepete ekleme işlemi
  const handleAddToCart = (productId) => {
    console.log(`Product ${productId} added to cart`); // Sepete ekleme işlemi burada yapılacak
  };

  // Eğer veri yükleniyorsa veya hata varsa uygun mesajları göster
  if (loading) {
    return <p>Loading products...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      {/* Arama ve Filtreleme bileşenini buraya ekliyoruz */}
      <SearchAndFilter onSearch={handleSearch} onFilter={handleFilter} />

      {/* Filtrelenmiş ürünleri listeleyelim */}
      <div className="product-list">
        {filteredProducts.length > 0 ? (
          filteredProducts.map(product => (
            <ProductCard key={product.id} product={product} onAdd={handleAddToCart} />
          ))
        ) : (
          <p>No products found</p> // Ürün bulunamazsa bu mesajı göster
        )}
      </div>
    </div>
  );
};

export default ProductList;
