import React, { useState } from 'react';
import axios from 'axios';
import './AddMerchant.css';  // CSS dosyasını import et

const AddMerchant = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const newMerchant = { name, email };
    setLoading(true);

    try {
      const response = await axios.post('https://localhost:7103/api/merchant', newMerchant);
      console.log('Merchant added:', response.data);
      setName('');
      setEmail('');
      setError(null);
    } catch (err) {
      console.error('Error adding merchant:', err);
      setError('Merchant eklenirken bir hata oluştu!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-merchant-container">
      <h2>Add Merchant</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input 
            type="text" 
            value={name} 
            onChange={(e) => setName(e.target.value)} 
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input 
            type="email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Adding...' : 'Add Merchant'}
        </button>
      </form>
      {error && <p className="error-message">{error}</p>}
      {loading && <div className="loading-indicator">Adding Merchant</div>}
    </div>
  );
};

export default AddMerchant;
