import React, { useState } from 'react';
import './AddToCartButton.css';

const AddToCartButton = ({ productId, onAdd }) => {
  const [addedToCart, setAddedToCart] = useState(false);

  const handleAddToCart = () => {
    setAddedToCart(true);
    onAdd(productId); // Sepete ekleme işlevi

    // 1 saniye sonra "addedToCart" state'ini false yaparak eski haline dönmesini sağla
    setTimeout(() => {
      setAddedToCart(false);
    }, 1300); // 1000 ms = 1 saniye
  };

  return (
    <button
      className={`add-to-cart-button ${addedToCart ? 'added' : ''}`}
      onClick={handleAddToCart}
    >
      {addedToCart ? 'Added to Cart' : 'Add to Cart'}
    </button>
  );
};

export default AddToCartButton;
