import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MerchantList = () => {
  const [merchants, setMerchants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Satıcıları API'den çekiyoruz
  useEffect(() => {
    axios.get('https://localhost:7103/api/merchant')
      .then(response => {
        setMerchants(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Error fetching merchants');
        setLoading(false);
      });
  }, []);

  const handleDeleteMerchant = (id) => {
    axios.delete(`https://localhost:7103/api/merchant/${id}`)
      .then(response => {
        // Silinen satıcıyı listeden çıkarıyoruz
        setMerchants(merchants.filter(merchant => merchant.merchantId !== id)); 
      })
      .catch(err => {
        console.error('Error deleting merchant:', err);
      });
  };
  

  if (loading) {
    return <p>Loading merchants...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      <h2>Merchant List</h2>
      <ul>
      {merchants.map(merchant => (
  <li key={merchant.merchantId}>  {/* merchant.id yerine merchant.merchantId kullanıyoruz */}
    {merchant.name} - {merchant.email}
    <button onClick={() => handleDeleteMerchant(merchant.merchantId)}>Delete</button>
  </li>
))}

      </ul>
    </div>
  );
};

export default MerchantList;
